(function ($) {
	function downloadUrl(user, groupId, artifactId, version, type) {
		var groupPath = groupId.replace(/\./g, "/");
		return 'https://dl.bintray.com/'+user+'/maven/' + groupPath + "/" + artifactId + "/" + version + "/" + artifactId + "-" + version + type;
	}
	$.fn.bintrayArtifactVersion = function (user, groupId, artifact, callback) {
			var version = '0.1.0';
			var type = '.jar';
			if ( artifact == 'library' ){
				version = '0.1.0';
				type = '.aar';
			}
			callback(version, downloadUrl(user, groupId, artifact, version, type));
	}
})(jQuery);
